import React, { useEffect, useContext, useState } from 'react';
import { NavLink, useNavigate } from "react-router-dom";
import { Context } from "../..";
import jwtDecode from "jwt-decode";
import Header from '../Header/Header';
import Footer from '../Footer/Footer';
import Buttons from '../Buttons/Buttons';
import ToTop from '../ToTop/ToTop';
import './Polls.css';
import banner from '../../img/pools/animals/banner.png';

const Animals = () => {
    
    const [loading, setLoadnig] = useState(false);
    const navigate = useNavigate();
    const { user } = useContext(Context);
    
    useEffect(() => {
        document.title = 'Опросы про животных';
      }, []);

      useEffect(() => {
        if (localStorage.getItem("token") != null) {
          const userinfo = jwtDecode(localStorage.getItem("token"));
          setLoadnig(true);
        } else {
          setLoadnig(true);
        }
      }, [user]);
    
      if (loading === false) {
        return <h1>Загрузка</h1>;
      }

    return (
        <div>
            <Header />
            <Buttons />
            <main className='main-container'>
            {/* 1 секция баннер */}
            <section className='main-banner'>
                <div className='banner'>
                    <div className='banner-title'>
                        <strong>Опросы про животных</strong>
                    </div>
                    <div className='banner-subtitle'>
                        <p>Опросы помогают лучше понять потребности и предпочтения людей в отношении животного мира. Результаты могут помочь улучшить условия 
                содержания животных и разработать более эффективные стратегии их защиты</p>
                    </div>
                </div>
                <div className='banner-img'>
                    <img src={banner} alt='' className='banner-img-img'/>
                </div>
            </section>
            {/* 2 секция */}
            <section className="Opros_1">
                <form className="opros">
                    <h3>Опрос №1</h3>
                    <fieldset className="firstVopros">
                    <legend>Как Вы относитесь к бродячим животным?</legend>
                    <div>
                        <input type="radio" id="q1a1" name="q1" defaultValue="Хорошо" defaultChecked=""/>
                        <label htmlFor="q1a1">Хорошо</label>
                    </div>
                    <div>
                        <input type="radio" id="q1a2" name="q1" defaultValue="Нейтрально" />
                        <label htmlFor="q1a2">Нейтрально</label>
                    </div>
                    <div>
                        <input type="radio" id="q1a3" name="q1" defaultValue="Отрицательно" />
                        <label htmlFor="q1a3">Отрицательно</label>
                    </div>
                    <div>
                        <input type="radio" id="q1a4" name="q1" defaultValue="Не знаю" />
                        <label htmlFor="q1a4">Не знаю</label>
                    </div>
                    </fieldset><br />
                    <fieldset className="secondVopros">
                    <legend>Вы довольны работой службы отлова?</legend>
                    <div>
                        <input type="radio" id="q2a1" name="q2" defaultValue="Да" defaultChecked=""/>
                        <label htmlFor="q2a1">Да</label>
                    </div>
                    <div>
                        <input type="radio" id="q2a2" name="q2" defaultValue="Нет" />
                        <label htmlFor="q2a2">Нет</label>
                    </div>
                    <div>
                        <input type="radio" id="q2a2" name="q2" defaultValue="Не знаю" />
                        <label htmlFor="q2a2">Не знаю</label>
                    </div>
                    </fieldset><br />
                    <fieldset className="thirdVopros">
                    <legend>Были ли у Вас, или знакомых, случаи нападения бездомных животных?</legend>
                    <div>
                        <input type="radio" id="q3a1" name="q3" defaultValue="Да" defaultChecked=""/>
                        <label htmlFor="q3a1">Да</label>
                    </div>
                    <div>
                        <input type="radio" id="q3a2" name="q3" defaultValue="Нет" />
                        <label htmlFor="q3a2">Нет</label>
                    </div>
                    <div>
                        <input type="radio" id="q3a3" name="q3" defaultValue="Не знаю" />
                        <label htmlFor="q3a3">Не знаю</label>
                    </div>
                    </fieldset><br />
                {user._isAuth === true && (
                <button id="submitFirst" className='submitFirst-button'>Отправить</button>
                )}
                </form>
            </section>
            </main>
            <ToTop />
            <Footer />
        </div>
    );
};

export default Animals;
