import React, { useState } from "react";
import jwtDecode from "jwt-decode";
import { AddProductToServer } from "../../queries/mainQuery";

const Animals = () => {
  const [name, setName] = useState("");
  const [img, setImg] = useState(null);
  const [country, setCountry] = useState("");
  const [price, setPrice] = useState("");
  const [year, setYear] = useState("");
  const [model, setModel] = useState("");
  const [id_category, setCategory] = useState("");
  const [cat, setCat] = useState([]);
  const [quantity, setQuantity] = useState("");
  const [update, setUpdate] = useState(false);

  const ChangeCategory = (e) => {
    setCategory(e.target.value);
  };

  const setFile = (e) => {
    setImg(e.target.files[0]);
    console.log(e.target.files[0]);
  };

  const AddProduct = async () => {
    const userRole = jwtDecode(localStorage.getItem("token"));
    if (userRole.role === "ADMIN") {
      const formdata = new FormData();
      formdata.append("name", name);
      formdata.append("img", img);
      formdata.append("country", country);
      formdata.append("price", price);
      formdata.append("year", year);
      formdata.append("model", model);
      formdata.append("id_category", id_category);
      formdata.append("quantity", quantity);
      await AddProductToServer(formdata);
      alert("Новый товар добавлен!");
    } else {
      alert("Ты не администратор!");
    }
  };
  console.log(cat);

  return (
    <div className="main__regis">
      <span className="zagolovok">Добавить товар</span>
      <div className="pp">
        <span className="p2">Название</span>
        <div>
          <input
            className="inp"
            type="text"
            onChange={(e) => setName(e.target.value)}
          />
        </div>
      </div>
      <div className="pp">
        <span className="p2">Фото</span>
        <div>
          <input className="inpp" type="file" onChange={setFile} />
        </div>
      </div>
      <div className="pp">
        <span className="p2">Страна</span>
        <div>
          <input
            className="inp"
            type="text"
            onChange={(e) => setCountry(e.target.value)}
          />
        </div>
      </div>
      <div className="pp">
        <span className="p2">Цена</span>
        <div>
          <input
            className="inp"
            type="text"
            onChange={(e) => setPrice(e.target.value)}
          />
        </div>
      </div>
      <div className="pp">
        <span className="p2">Год</span>
        <div>
          <input
            className="inp"
            type="text"
            onChange={(e) => setYear(e.target.value)}
          />
        </div>
      </div>
      <div className="pp">
        <span className="p2">Модель</span>
        <div>
          <input
            className="inp"
            type="text"
            onChange={(e) => setModel(e.target.value)}
          />
        </div>
      </div>
      <div className="pp">
        <span className="p2">Категория</span>
        <div>
          {/* <input
            className="inp"
            type="text"
            onChange={(e) => setCategory(e.target.value)}
          /> */}
          <Select options={cat} onChange={ChangeCategory} />
        </div>
      </div>
      <div className="pp">
        <span className="p2">Количество</span>
        <div>
          <input
            className="inp"
            type="text"
            onChange={(e) => setQuantity(e.target.value)}
          />
        </div>
      </div>
      <div>
        <button type="button" className="regis__btn" onClick={AddProduct}>
          Добавить товар!
        </button>
      </div>
    </div>
  );
};

export default Animals;
