import React, { useEffect, useContext, useState} from 'react';
// import { Context } from "../..";
// import Header from '../Header/Header';
// import Footer from '../Footer/Footer';
// import Buttons from '../Buttons/Buttons';
// import ToTop from '../ToTop/ToTop';
// import './Polls.css';
// import banner from '../../img/pools/animals/banner.png';


const Animals = () => {
  const [answer1, setAnswer1] = useState("");
  const [answer2, setAnswer2] = useState("");
  const [answer3, setAnswer3] = useState("");

  const handleAnswer1Change = (event) => {
    setAnswer1(event.target.value);
  };

  const handleAnswer2Change = (event) => {
    setAnswer2(event.target.value);
  };

  const handleAnswer3Change = (event) => {
    setAnswer3(event.target.value);
  };

  const handleSubmit = (event) => {
    event.preventDefault();

    // Отправка ответов на сервер
    const data = {
      answer1,
      answer2,
      answer3,
    };

    fetch("/save-answers", {
      method: "POST",
      headers: {
        "Content-Type": "application/json",
      },
      body: JSON.stringify(data),
    })
      .then((response) => response.json())
      .then((result) => {
        // Обработка ответа от сервера, например, показ сообщения об успешном сохранении
        console.log(result);
      })
      .catch((error) => {
        // Обработка ошибок
        console.error("Error saving answers:", error);
      });
  };

  return (
    <form className="opros" onSubmit={handleSubmit}>
      <h3>Опрос №1</h3>
      <fieldset className="firstVopros">
        <legend>
          Откуда Вы чаще всего узнаете о новостях, событиях из жизни Вашего
          региона, страны, мира?
        </legend>
        <div>
          <input
            type="radio"
            id="q1a1"
            name="q1"
            value="ТВ"
            checked={answer1 === "ТВ"}
            onChange={handleAnswer1Change}
          />
          <label htmlFor="q1a1">ТВ</label>
        </div>
        <div>
          <input
            type="radio"
            id="q1a2"
            name="q1"
            value="Социальные сети"
            checked={answer1 === "Социальные сети"}
            onChange={handleAnswer1Change}
          />
          <label htmlFor="q1a2">Социальные сети</label>
        </div>
        <div>
          <input
            type="radio"
            id="q1a3"
            name="q1"
            value="Другое"
            checked={answer1 === "Другое"}
            onChange={handleAnswer1Change}
          />
          <label htmlFor="q1a3">Другое</label>
        </div>
      </fieldset>
      <br />
      <fieldset className="secondVopros">
        <legend>Вы интересуетесь или не интересуетесь политикой?</legend>
        <div>
          <input
            type="radio"
            id="q2a1"
            name="q2"
            value="Да"
            checked={answer2 === "Да"}
            onChange={handleAnswer2Change}
          />
          <label htmlFor="q2a1">Да</label>
        </div>
        <div>
          <input
            type="radio"
            id="q2a2"
            name="q2"
            value="Нет"
            checked={answer2 === "Нет"}
            onChange={handleAnswer2Change}
          />
          <label htmlFor="q2a2">Нет</label>
        </div>
      </fieldset>
      <br />
      <fieldset className="thirdVopros">
        <legend>
          Какие новостные темы Вас интересуют? (выберите все подходящие варианты)
        </legend>
        <div>
          <input
            type="checkbox"
            id="q3a1"
            name="q3"
            value="Политика"
            checked={answer3.includes("Политика")}
            onChange={handleAnswer3Change}
          />
          <label htmlFor="q3a1">Политика</label>
        </div>
        <div>
          <input
            type="checkbox"
            id="q3a2"
            name="q3"
            value="Экономика"
            checked={answer3.includes("Экономика")}
            onChange={handleAnswer3Change}
          />
          <label htmlFor="q3a2">Экономика</label>
        </div>
        <div>
          <input
            type="checkbox"
            id="q3a3"
            name="q3"
            value="Наука и технологии"
            checked={answer3.includes("Наука и технологии")}
            onChange={handleAnswer3Change}
          />
          <label htmlFor="q3a3">Наука и технологии</label>
        </div>
      </fieldset>
      <br />
      <button type="submit">Отправить</button>
    </form>
  );
};

export default Animals;

