import React, { useState } from "react";

const Animals = ({ pollId }) => {
  const [answer1, setAnswer1] = useState("");
  const [answer2, setAnswer2] = useState("");
  const [answer3, setAnswer3] = useState("");

  const handleAnswer1Change = (event) => {
    setAnswer1(event.target.value);
  };

  const handleAnswer2Change = (event) => {
    setAnswer2(event.target.value);
  };

  const handleAnswer3Change = (event) => {
    setAnswer3(event.target.value);
  };

  const handleSubmit = () => {
    // Отправка ответов на сервер
    const data = {
      answer1,
      answer2,
      answer3,
      pollId,
    };

    fetch("/save-answers", {
      method: "POST",
      headers: {
        "Content-Type": "application/json",
      },
      body: JSON.stringify(data),
    })
      .then((response) => response.json())
      .then((result) => {
        // Обработка ответа от сервера
        console.log(result);
      })
      .catch((error) => {
        // Обработка ошибок
        console.error("Error saving answers:", error);
      });
  };

  return (
    <div>
      <h3>Опрос</h3>
      <div>
        <label htmlFor="answer1">Вариант ответа 1:</label>
        <input
          type="text"
          id="answer1"
          value={answer1}
          onChange={handleAnswer1Change}
        />
      </div>
      <div>
        <label htmlFor="answer2">Вариант ответа 2:</label>
        <input
          type="text"
          id="answer2"
          value={answer2}
          onChange={handleAnswer2Change}
        />
      </div>
      <div>
        <label htmlFor="answer3">Вариант ответа 3:</label>
        <input
          type="text"
          id="answer3"
          value={answer3}
          onChange={handleAnswer3Change}
        />
      </div>
      <button onClick={handleSubmit}>Отправить</button>
    </div>
  );
};

export default Animals;
